      <div className="ui grid">
        <div className="ui row">
          <div className="eleven wide column">
            <VideoDetail video={selectedVideo} />
          </div>
          <div className="five wide column">
            <VideoList videos={videos} onVideoSelected={onVideoSelected} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
